// Liste.js
import React, { useState } from 'react';
import Card from './Card'; // Assurez-vous d'importer le composant Card

function Liste() {
  const [personnes, setPersonnes] = useState([]);

  const handleFormSubmit = (nom) => {
    // Ajouter le nom à la liste des personnes
    setPersonnes([...personnes, nom]);
  };

  return (
    <div>
      <h1>Liste de personnes</h1>
      <div className="card-list">
        {personnes.map((personne, index) => (
          <Card key={index} text={personne} />
        ))}
      </div>
      {/* Passer la fonction de soumission du formulaire au composant Formulaire */}
      <Formulaire onFormSubmit={handleFormSubmit} />
    </div>
  );
}

export default Liste;
